mapper.select:
SELECT contract_code, head_staff_id, sub_code, contractor_name, contract_actual_date, contract_regit_date, contract_expiry_date, contract_deposit, contract_profit_percent, contract_file, contract_n, contract_expire, contract_expired_date, contract_expire_content
FROM contract

	
		date(contract_actual_date) >= #{contractSearch.regitDateStart} and date(contract_actual_date) <= #{contractSearch.regitDateEnd}  
	
	
		AND  ${contractSearch.searchKey} like CONCAT('%',#{contractSearch.searchItem},'%') 
	 
	
		AND  contract_expire = #{contractSearch.status}
	 
 

	
		contract_actual_date
	
	
		${contractSearch.criteria} ${contractSearch.upDown}

SELECT contract_code, head_staff_id, sub_code, contractor_name, contract_actual_date, contract_regit_date, contract_expiry_date, contract_deposit, contract_profit_percent, contract_file, contract_n, contract_expire, contract_expired_date, contract_expire_content
FROM contract
WHERE contract_code = #{contractCode}

mapper.update:
UPDATE contract
SET
	contract_expire='Y',
	contract_expired_date=NOW(),
	contract_expire_content=#{contract.contractExpireContent}
WHERE contract_code = #{contract.contractCode}
