mapper.select:
SELECT head_staff_id, head_staff_pw, head_staff_name, head_staff_level, head_staff_dep, head_staff_join, head_staff_resign, head_staff_regit_date, head_staff_regit_id, head_staff_resign_identify From head_staff where head_staff_resign_identify = #{headStaff.resignIdentify}
AND ${headStaffSearch.searchHeadStaffKey} like CONCAT('%',#{headStaffSearch.searchHeadStaffInfo},'%')
head_staff_join DESC
${headStaffSearch.criteria} ${headStaffSearch.upDown}

mapper.update:

UPDATE
	head_staff
 SET	
	head_staff_name = #{headStaffName},
	head_staff_level = #{headStaffLevel},
	head_staff_dep = #{headStaffDep}
head_staff_resign = #{headStaffRegitId} 
WHERE  
	head_staff_id = #{headStaffId}
