mapper.select:
SELECT specific_item_code, h_item_code, head_buy_date, head_staff_id, head_stock_regit_date, head_stock_item_out, head_stock_in_date, head_stock_in_expiry_date, head_stock_return From head_stock
AND ${search.searchKey} like CONCAT('%',#{search.searchItem},'%')
${search.criteria} ${search.upDown}

SELECT count(specific_item_code)
FROM head_stock
WHERE h_item_code = #{hItemCode}
